INSERT INTO board_posts (user_id, plan_id, hotplace_id, title, content, region_code, trip_type, season, view_count, like_count, is_public, category, created_at, updated_at) VALUES
(11, NULL, NULL, '부산 3박 4일 여행 계획 공유', '부산 해운대, 광안리, 감천문화마을을 둘러보는 3박 4일 코스입니다. 맛집과 카페도 많이 추천드려요!', '6', '문화', '봄', 145, 23, TRUE, 'TRAVEL_PLAN', '2024-03-15 10:30:00', '2024-03-15 10:30:00'),
(12, NULL, NULL, '제주도 4박 5일 로맨틱 여행', '커플 여행에 최적인 제주도 코스입니다. 성산일출봉, 카멜리아힐, 협재해수욕장 등을 포함한 로맨틱한 일정이에요!', '1', '휴양', '여름', 289, 45, TRUE, 'TRAVEL_PLAN', '2024-06-20 14:20:00', '2024-06-20 14:20:00'),
(13, NULL, NULL, '서울 한 달 살기 완벽 가이드', '서울에서 한 달 동안 살면서 느낀 추천 장소들과 일정을 공유합니다. 마포, 홍대, 이태원 중심으로 구성했어요.', '1', '문화', '가을', 432, 67, TRUE, 'TRAVEL_PLAN', '2024-09-10 09:15:00', '2024-09-10 09:15:00'),
(14, NULL, NULL, '전주 한옥마을 2박 3일 여행', '전통 한옥 숙박과 맛집 투어를 포함한 전주 여행 계획입니다. 비빔밥, 한옥마을, 경기전까지!', '5', '문화', '가을', 178, 31, TRUE, 'TRAVEL_PLAN', '2024-10-05 16:45:00', '2024-10-05 16:45:00'),
(15, NULL, NULL, '강릉 커피거리와 안목해변', '강릉 커피거리와 안목해변을 중심으로 한 1박 2일 여행입니다. 바다 카페에서의 힐링 타임 추천!', '32', '휴양', '여름', 267, 38, TRUE, 'TRAVEL_PLAN', '2024-07-12 11:30:00', '2024-07-12 11:30:00');
INSERT INTO board_posts (user_id, plan_id, hotplace_id, title, content, region_code, trip_type, season, view_count, like_count, is_public, category, created_at, updated_at) VALUES
(16, NULL, NULL, '제주도 여행 후기 - 바다와 하늘의 조화', '<p>제주도에서 보낸 3박 4일의 소중한 시간들을 기록했습니다. 특히 성산일출봉에서 본 일출은 잊을 수 없어요. 협재해수욕장의 투명한 바다도 정말 인상적이었습니다.</p><img src="https://images.unsplash.com/photo-1507525428034-b723cf961d3e?w=600&h=400&fit=crop" alt="제주도 바다" style="max-width: 100%; height: auto; margin: 10px 0; border-radius: 8px;" /><img src="https://images.unsplash.com/photo-1518837695005-2083093ee35b?w=600&h=400&fit=crop" alt="제주 일출" style="max-width: 100%; height: auto; margin: 10px 0; border-radius: 8px;" />', '1', '휴양', '여름', 312, 52, TRUE, 'TRAVEL_RECORD', '2024-08-01 20:00:00', '2024-08-01 20:00:00'),
(11, NULL, NULL, '부산 해운대에서의 특별한 하루', '<p>부산 여행 중 가장 기억에 남는 하루입니다. 해운대 해수욕장에서 산책하고, 광안리에서 저녁을 먹으며 보낸 시간이 너무 행복했어요. 감천문화마을의 컬러풀한 골목길도 인상 깊었습니다.</p><img src="https://images.unsplash.com/photo-1559827260-dc66d52bef19?w=800" alt="부산 해운대" style="max-width: 100%; height: auto; margin: 10px 0; border-radius: 8px;" /><img src="https://images.unsplash.com/photo-1565851419440-2e40c6b2e1be?w=800" alt="감천문화마을" style="max-width: 100%; height: auto; margin: 10px 0; border-radius: 8px;" />', '6', '문화', '봄', 189, 28, TRUE, 'TRAVEL_RECORD', '2024-04-18 19:30:00', '2024-04-18 19:30:00'),
(17, NULL, NULL, '가을 단풍 여행 - 설악산 후기', '<p>설악산의 가을 단풍을 보러 다녀왔습니다. 금강산 정상까지 등반했고, 정상에서 내려다본 단풍의 풍경은 말로 표현할 수 없었어요. 가을 여행 추천합니다!</p><img src="https://images.unsplash.com/photo-1506905925346-21bda4d32df4?w=800" alt="설악산 단풍" style="max-width: 100%; height: auto; margin: 10px 0; border-radius: 8px;" /><img src="https://images.unsplash.com/photo-1501594907352-04cda38ebc29?w=800" alt="가을 단풍" style="max-width: 100%; height: auto; margin: 10px 0; border-radius: 8px;" />', '32', '자연', '가을', 245, 41, TRUE, 'TRAVEL_RECORD', '2024-10-25 15:20:00', '2024-10-25 15:20:00'),
(12, NULL, NULL, '전주 한옥마을에서 보낸 전통문화 체험', '<p>전주 한옥마을에서 한옥 스테이를 하며 전통 문화를 체험했습니다. 전통 한복도 입어보고, 전주 비빔밥과 한정식도 맛보았어요. 정말 의미 있는 시간이었습니다.</p><img src="https://images.unsplash.com/photo-1578662996442-48f60103fc96?w=800" alt="전주 한옥마을" style="max-width: 100%; height: auto; margin: 10px 0; border-radius: 8px;" /><img src="https://images.unsplash.com/photo-1512621776951-a57141f2eefd?w=800" alt="전통 한옥" style="max-width: 100%; height: auto; margin: 10px 0; border-radius: 8px;" />', '5', '문화', '가을', 156, 24, TRUE, 'TRAVEL_RECORD', '2024-10-12 12:10:00', '2024-10-12 12:10:00'),
(18, NULL, NULL, '강릉 커피거리 투어 - 카페 홀릭의 기록', '<p>강릉 커피거리를 중심으로 여러 카페를 탐방했습니다. 안목해변 카페에서 바다를 보며 마신 커피는 최고였어요. 커피 애호가라면 강력 추천합니다!</p><img src="https://images.unsplash.com/photo-1447933601403-0c6688de566e?w=800" alt="강릉 안목해변" style="max-width: 100%; height: auto; margin: 10px 0; border-radius: 8px;" /><img src="https://images.unsplash.com/photo-1495474472287-4d71bcdd2085?w=800" alt="커피 카페" style="max-width: 100%; height: auto; margin: 10px 0; border-radius: 8px;" />', '32', '휴양', '여름', 223, 35, TRUE, 'TRAVEL_RECORD', '2024-07-28 17:45:00', '2024-07-28 17:45:00');
INSERT INTO board_posts (user_id, plan_id, hotplace_id, title, content, region_code, trip_type, season, view_count, like_count, is_public, category, created_at, updated_at) VALUES
(13, NULL, 3, '서울 성수동 카페거리 핫플', '<p>성수동에 있는 최고의 카페들을 소개합니다. 특히 뷰가 좋은 카페들이 많아서 사진 찍기 좋은 곳들이 많아요!</p><img src="https://images.unsplash.com/photo-1511920170033-f8396924c348?w=800" alt="성수동 카페" style="max-width: 100%; height: auto; margin: 10px 0; border-radius: 8px;" /><img src="https://images.unsplash.com/photo-1495474472287-4d71bcdd2085?w=800" alt="카페 인테리어" style="max-width: 100%; height: auto; margin: 10px 0; border-radius: 8px;" />', '1', '문화', '봄', 198, 29, TRUE, 'HOTPLACE', '2024-04-02 13:20:00', '2024-04-02 13:20:00'),
(14, NULL, 2, '제주 협재해수욕장 - 투명한 바다의 경이로움', '<p>제주도 협재해수욕장의 투명한 바다를 여러분께 소개합니다. 여름에 가면 정말 시원하고 아름다워요. 주변에 카페와 맛집도 많습니다!</p><img src="https://images.unsplash.com/photo-1507525428034-b723cf961d3e?w=600&h=400&fit=crop" alt="협재해수욕장" style="max-width: 100%; height: auto; margin: 10px 0; border-radius: 8px;" /><img src="https://images.unsplash.com/photo-1518837695005-2083093ee35b?w=600&h=400&fit=crop" alt="투명한 바다" style="max-width: 100%; height: auto; margin: 10px 0; border-radius: 8px;" />', '1', '휴양', '여름', 345, 58, TRUE, 'HOTPLACE', '2024-07-05 10:00:00', '2024-07-05 10:00:00'),
(15, NULL, 4, '부산 감천문화마을 - 컬러풀한 골목길', '<p>부산 감천문화마을의 다채로운 골목길을 탐험했습니다. 사진 찍기 좋은 곳들이 많고, 작은 카페와 공방들도 많아서 둘러보기 좋았어요!</p><img src="https://images.unsplash.com/photo-1502602898657-3e91760cbb34?w=600&h=400&fit=crop" alt="감천문화마을" style="max-width: 100%; height: auto; margin: 10px 0; border-radius: 8px;" /><img src="https://images.unsplash.com/photo-1539650116574-75c0c6d73a8e?w=600&h=400&fit=crop" alt="컬러풀한 골목" style="max-width: 100%; height: auto; margin: 10px 0; border-radius: 8px;" />', '6', '문화', '봄', 267, 42, TRUE, 'HOTPLACE', '2024-03-28 14:30:00', '2024-03-28 14:30:00'),
(16, NULL, 5, '강릉 안목해변 카페거리', '<p>강릉 안목해변에 있는 바다뷰 카페들을 소개합니다. 바다를 보며 마시는 커피는 정말 최고예요! 일몰 시간대에 가시는 것을 추천합니다.</p><img src="https://images.unsplash.com/photo-1447933601403-0c6688de566e?w=800" alt="안목해변" style="max-width: 100%; height: auto; margin: 10px 0; border-radius: 8px;" /><img src="https://images.unsplash.com/photo-1511920170033-f8396924c348?w=800" alt="바다뷰 카페" style="max-width: 100%; height: auto; margin: 10px 0; border-radius: 8px;" />', '32', '휴양', '여름', 289, 47, TRUE, 'HOTPLACE', '2024-08-10 16:15:00', '2024-08-10 16:15:00'),
(17, NULL, 6, '전주 한옥마을 - 전통과 현대의 만남', '<p>전주 한옥마을에서 발견한 특별한 장소들을 공유합니다. 전통 한옥과 현대적인 카페가 공존하는 독특한 분위기가 인상적이었어요.</p><img src="https://images.unsplash.com/photo-1578662996442-48f60103fc96?w=800" alt="전주 한옥마을" style="max-width: 100%; height: auto; margin: 10px 0; border-radius: 8px;" /><img src="https://images.unsplash.com/photo-1512621776951-a57141f2eefd?w=800" alt="전통 한옥" style="max-width: 100%; height: auto; margin: 10px 0; border-radius: 8px;" />', '5', '문화', '가을', 178, 33, TRUE, 'HOTPLACE', '2024-09-22 11:50:00', '2024-09-22 11:50:00'),
(18, NULL, 7, '서울 이태원 - 국제적인 분위기의 거리', '<p>이태원의 다양한 레스토랑과 바를 소개합니다. 세계 각국의 맛을 한 곳에서 즐길 수 있는 곳이에요!</p><img src="https://images.unsplash.com/photo-1517248135467-4c7edcad34c4?w=800" alt="이태원 거리" style="max-width: 100%; height: auto; margin: 10px 0; border-radius: 8px;" /><img src="https://images.unsplash.com/photo-1514933651103-005eec06c04b?w=800" alt="이태원 레스토랑" style="max-width: 100%; height: auto; margin: 10px 0; border-radius: 8px;" />', '1', '문화', '가을', 234, 39, TRUE, 'HOTPLACE', '2024-10-08 18:20:00', '2024-10-08 18:20:00'),
(11, NULL, 8, '제주 성산일출봉 - 일출 명소', '<p>제주도 성산일출봉에서 본 일출의 풍경을 소개합니다. 새벽에 올라가서 본 일출은 정말 장관이었어요. 가시는 분들은 일찍 일어나세요!</p><img src="https://images.unsplash.com/photo-1518837695005-2083093ee35b?w=600&h=400&fit=crop" alt="성산일출봉 일출" style="max-width: 100%; height: auto; margin: 10px 0; border-radius: 8px;" /><img src="https://images.unsplash.com/photo-1469474968028-56623f02e42e?w=600&h=400&fit=crop" alt="일출 풍경" style="max-width: 100%; height: auto; margin: 10px 0; border-radius: 8px;" />', '1', '자연', '여름', 412, 71, TRUE, 'HOTPLACE', '2024-08-15 06:30:00', '2024-08-15 06:30:00'),
(12, NULL, 9, '부산 광안리 해수욕장 - 밤의 불꽃놀이', '<p>부산 광안리 해수욕장에서 본 멋진 야경을 공유합니다. 특히 해변가 레스토랑에서 바라본 밤 풍경이 최고였어요!</p><img src="https://images.unsplash.com/photo-1559827260-dc66d52bef19?w=800" alt="광안리 야경" style="max-width: 100%; height: auto; margin: 10px 0; border-radius: 8px;" /><img src="https://images.unsplash.com/photo-1547036967-23d11aacaee0?w=800" alt="부산 해변 야경" style="max-width: 100%; height: auto; margin: 10px 0; border-radius: 8px;" />', '6', '휴양', '여름', 298, 44, TRUE, 'HOTPLACE', '2024-07-20 20:45:00', '2024-07-20 20:45:00');
